commits.content:
.modal-backdrop {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
}

.modal-content {
  background: white;
  padding: 2rem;
  border-radius: 8px;
  width: 90%;
  max-width: 600px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
  position: relative;
  animation: fadeIn 0.3s ease-out;
}

@keyframes fadeIn {
  from { opacity: 0; transform: scale(0.9); }
  to { opacity: 1; transform: scale(1); }
}

.modal-close-btn {
  position: absolute;
  top: 10px;
  right: 15px;
  background: transparent;
  border: none;
  font-size: 2rem;
  color: #aaa;
  cursor: pointer;
}

.modal-section {
  margin-top: 1.5rem;
}

.modal-section strong {
  color: #333;
}

.modal-section p {
  background: #f9f9f9;
  border: 1px solid #eee;
  padding: 10px;
  border-radius: 5px;
  margin-top: 0.5rem;
}

.modal-suggestion {
  background: #e6f7ff !important;
  border-color: #b3e0ff !important;
}

.modal-error {
  background: #fff0f0 !important;
  border-color: #ffcccc !important;
  color: #d9534f;
}
