import React from 'react';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

// This component receives the 'score' (a number 0-100) as a prop
function ScoreDonut({ score }) {
  // Determine color based on score
  const getPathColor = (percentage) => {
    if (percentage > 75) return '#d9534f'; // Red for high plagiarism
    if (percentage > 40) return '#f0ad4e'; // Yellow for medium
    return '#5cb85c'; // Green for low/original
  };

  const scoreColor = getPathColor(score);

  return (
    <div style={{ width: 200, height: 200, margin: 'auto' }}>
      <CircularProgressbar
        value={score}
        text={`${score}%`}
        styles={buildStyles({
          // Rotation of path and trail, in number of turns (0-1)
          rotation: 0.25,

          // Whether to use rounded or flat stroke end caps
          strokeLinecap: 'butt',

          // Text size
          textSize: '16px',

          // How long animation takes to go from one percentage to another, in seconds
          pathTransitionDuration: 0.5,

          // Colors
          pathColor: scoreColor,
          textColor: scoreColor,
          trailColor: '#d6d6d6',
          backgroundColor: '#3e98c7',
        })}
      />
      <h3 style={{ textAlign: 'center', color: scoreColor, marginTop: '10px' }}>
        Plagiarism Score
      </h3>
    </div>
  );
}

export default ScoreDonut;
