import React from "react";
import { Doughnut } from "react-chartjs-2";
import "chart.js/auto";

const PlagiarismResult = ({ results }) => {
  if (!results || results.length === 0) {
    return <p className="text-center text-gray-500">No analysis results available.</p>;
  }

  // Count each type
  const counts = { direct: 0, paraphrased: 0, unique: 0 };
  results.forEach((r) => {
    if (r.type === "direct") counts.direct++;
    else if (r.type === "paraphrased") counts.paraphrased++;
    else counts.unique++;
  });

  // Chart data
  const data = {
    labels: ["Direct", "Paraphrased", "Unique"],
    datasets: [
      {
        data: [counts.direct, counts.paraphrased, counts.unique],
        backgroundColor: ["#ff4d4d", "#ffcc00", "#4caf50"],
        borderWidth: 1,
      },
    ],
  };

  // Highlight sentence
  const highlight = (r) => {
    if (r.type === "direct") {
      return <span style={{ backgroundColor: "#ff4d4d33", color: "#b30000" }}>{r.sentence} </span>;
    } else if (r.type === "paraphrased") {
      return <span style={{ backgroundColor: "#fff2cc", color: "#b38f00" }}>{r.sentence} </span>;
    } else {
      return <span>{r.sentence} </span>;
    }
  };

  return (
    <div className="flex flex-col lg:flex-row gap-6 mt-6">
      <div className="flex-1 bg-gray-50 p-4 rounded-md max-h-[70vh] overflow-y-auto shadow-sm">
        <h3 className="text-xl font-semibold mb-3">Uploaded Text</h3>
        <p className="text-gray-800 leading-7 text-justify">
          {results.map((r, i) => (
            <React.Fragment key={i}>{highlight(r)}</React.Fragment>
          ))}
        </p>
      </div>

      <div className="w-full lg:w-1/3 bg-white p-4 rounded-md shadow-md">
        <h3 className="text-xl font-semibold mb-3 text-center">Plagiarism Summary</h3>
        <Doughnut data={data} />
        <div className="mt-4 text-center space-y-1">
          <p><strong className="text-red-600">Direct:</strong> {counts.direct}</p>
          <p><strong className="text-yellow-600">Paraphrased:</strong> {counts.paraphrased}</p>
          <p><strong className="text-green-600">Unique:</strong> {counts.unique}</p>
        </div>
      </div>
    </div>
  );
};

export default PlagiarismResult;
