commits.content:
.report-text-block {
  line-height: 2;
  font-size: 1.1rem;
}

.sentence {
  transition: background-color 0.3s ease;
  padding: 2px 0;
}

.highlight-red {
  background-color: #ff000033; /* Red with transparency */
  border-bottom: 2px solid #e60000;
}

.highlight-yellow {
  background-color: #ffff0033; /* Yellow with transparency */
  border-bottom: 2px solid #e6e600;
}

.original {
  background-color: transparent;
}

.rewrite-btn {
  /* A small, subtle button */
  margin-left: 5px;
  padding: 2px 6px;
  font-size: 0.7rem;
  border: 1px solid #007bff;
  background: #007bff;
  color: white;
  border-radius: 4px;
  cursor: pointer;
  opacity: 0.7;
  transition: opacity 0.2s;
}

.sentence:hover .rewrite-btn {
  opacity: 1;
}
