import React from 'react';
import './ReportVisualizer.css'; // Don't forget to create this CSS file

// This component is "dumb". It just receives data (props) and displays it.
// It receives 'sentences' and 'onRewriteClick' from its parent (Dashboard.js)
function ReportVisualizer({ sentences, onRewriteClick }) {
  
  // Helper function to get the right CSS class
  const getHighlightClass = (match)_ => {
    if (!match) return 'original';
    if (match.type === 'Direct Match') return 'highlight-red';
    if (match.type === 'Paraphrased') return 'highlight-yellow';
    return 'original';
  };

  return (
    <div className="report-visualizer">
      <h2>Detailed Report</h2>
      <p className="report-text-block">
        {sentences.map((item, index)_ => (
          <span 
            key={index} 
            className={`sentence ${getHighlightClass(item.match)}`}
            title={item.match ? `Source: ${item.match.source}` : 'Original'}
          >
            {item.text}{' '}
            
            {/* If it's a match, show the rewrite button */}
            {item.match && (
              <button 
                className="rewrite-btn"
                onClick={() => onRewriteClick(item.text)} // Calls the function from the parent
              >
                Rewrite
              </button>
            )}
          </span>
        ))}
      </p>
    </div>
  );
}

export default ReportVisualizer;
