import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

// Add some basic styling
const homeStyles = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  padding: '2rem',
  fontFamily: 'Arial, sans-serif'
};

const textareaStyles = {
  width: '80%',
  minHeight: '300px',
  padding: '10px',
  fontSize: '1rem',
  borderRadius: '8px',
  border: '1px solid #ccc'
};

const buttonStyles = {
  marginTop: '1rem',
  padding: '10px 20px',
  fontSize: '1.1rem',
  backgroundColor: '#007bff',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer'
};

function Home() {
  const [text, setText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = () => {
    if (!text.trim()) {
      setError('Please enter some text to analyze.');
      return;
    }

    setIsLoading(true);
    setError('');

    // Call your Flask NLP service
    axios.post('http://localhost:5001/api/analyze', { text })
      .then(response => {
        setIsLoading(false);
        // On success, navigate to the dashboard and pass the report data
        navigate('/dashboard', { state: { report: response.data } });
      })
      .catch(err => {
        setIsLoading(false);
        console.error('Error analyzing document:', err);
        setError('Failed to analyze document. Please check the console.');
      });
  };

  return (
    <div style={homeStyles}>
      <h1>Authintic Plagiarism Checker</h1>
      <p>Paste your text below to check for plagiarism.</p>
      
      <textarea
        style={textareaStyles}
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Start writing or paste your text here..."
      />
      
      <button 
        style={buttonStyles} 
        onClick={handleSubmit} 
        disabled={isLoading}
      >
        {isLoading ? 'Analyzing...' : 'Analyze Text'}
      </button>

      {error && <p style={{ color: 'red', marginTop: '1rem' }}>{error}</p>}
    </div>
  );
}

export default Home;
